import React, { Component, Fragment } from 'react';

function CatCard({    name,birthdate,race,gender,city,description,image
}){
    const send_adoption=()=>{
        this.setState({...this.state, icon:"schedule",backgroundColor:"grey"})

        console.log("hoho")
    }
    const openModal=()=>{
        this.setState({...this.state, displayModal:"block"})
    }
    closeModal=()=>{
        this.setState({...this.state, displayModal:"none"})
    }
    return (
            <Fragment>
                        <div className="card" >
                        <div className="top-card">
                                        <img src={image}></img>
                <p>{name}</p>
                        </div>
                        <div className="top-card">
                                        <div className="adopt-button" style={{backgroundColor:this.state.backgroundColor}} onClick={this.send_adoption}><i class="material-icons">{this.state.icon}</i>
                </div>
                <span onClick={this.openModal}> Plus de d√©tails</span>

                        </div>
            </div>
            <div className="modal-cat"  style={{display:this.state.displayModal}}>
                                <div className="adopt-button" style={{backgroundColor:"black"}} onClick={this.closeModal}
                                ><i class="material-icons">close</i>
            </div>
                                <img src={this.state.img}></img>
                <p>Nom du chat</p>

            </div>

            </Fragment>
        );
    }

export default CatCard;
