import React, { Component } from 'react';
import AccessTime from '@mui/material-icons/AccessTime';
class CatCard extends Component {
    state={img:""}
    componentDidMount(){
        fetch('http://aws.random.cat/meow')
        .then(response => response.json())
        .then(data => {
            console.log(data)
            this.setState({img:data.file})
        }
            )
    }
    render() {
        return (
            <div class="card">
                <img src={this.state.img}></img>
                <p>Nom du chat</p>
                <div class="adopt-button">+ <AccessTime/></div>
            </div>
        );
    }
}

export default CatCard;
