import React, { Component, Fragment } from 'react';

class CatCard extends Component {
    send_adoption=()=>{
        this.setState({...this.state, icon:"schedule",backgroundColor:"grey"})

        console.log("hoho")
    }
    openModal=()=>{
        this.setState({...this.state, displayModal:"block"})
    }
    state={img:"", icon:"add", backgroundColor:"red",displayModal:"none"}
    componentDidMount(){
        fetch('http://aws.random.cat/meow')
        .then(response => response.json())
        .then(data => {
            console.log(data)
            this.setState({...this.state, img:data.file})
        }
            )
    }
    render() {
        return (
            <Fragment>
                        <div className="card">
                <img src={this.state.img}></img>
                <p>Nom du chat</p>
                <div className="adopt-button" style={{backgroundColor:this.state.backgroundColor}} onClick={this.send_adoption}><i class="material-icons">{this.state.icon}</i>
</div>
            </div>
            <div className="modal-cat" onClick={this.openModal} style={{display:displayModal}}>
            </div>

            </Fragment>
        );
    }
}

export default CatCard;
