import React, { Component } from 'react';

class AdminBodyList extends Component {
    state={cats:[]}
    deleteCat=(index)=>{
        fetch(`http://localhost:5000/deletecat?index=${index}`)
        .then(response => response.json())
        .then(data => {
            console.log(data)
            this.setState({...this.state, cats:data})
            }
        )

    }
    async componentDidMount() {
        fetch('http://localhost:5000/getcats')
        .then(response => response.json())
        .then(data => {
            console.log(data)
            this.setState({...this.state, cats:data})
            }
        )

    }
    render() {
        return (
            <div className="admin-body">
                <table>
                    <tbody>
                        <tr className="table-header">
                        <th>IMAGE</th>
                <th>NOM</th>
        <th>DATE DE NAISSANCE</th>
        <th>RACE</th>
        <th>SEXE</th>
        <th>VILLE</th>
        <th></th>
        <th></th>
                        </tr>
                        {
this.state.cats.length>0?
this.state.cats.map((item,index) => (
    <tr>
    <td><img src={item.image} style={{height:'50px'}}/></td>
<td>{item.name}</td>
<td>{item.birthdate}</td>
<td>{item.race}</td>
<td>{item.gender}</td>
<td>{item.city}</td>
<td><i class="material-icons">edit</i>
</td>
<td><i onClick={this.deleteCat} style={{color:"red"}} class="material-icons">close</i>
</td>
    </tr>

)):<p>Il n'y a pas de chats, veuillez en rajouter dans la section Admin</p>

                    }


                    </tbody>
        </table>

            </div>
        );
    }
}

export default AdminBodyList;
