import React, { Component } from 'react';

class AdminBodyList extends Component {
    state={cats:[],currentcat:{},displayModal:"none"}


    handleChange=(event)=>{
        var {name,value}=event.target
        this.setState({...this.state, [name]:value})



    }
    handleSubmit=()=>{
        var errormessage=""
        if(this.state.name==""){
            errormessage=errormessage+"Veuillez entrer un nom \n"
        }
        if(this.state.birthdate==""){
            errormessage=errormessage+"Veuillez entrer une date de naissance \n"
        }
        if(this.state.race==""){
            errormessage=errormessage+"Veuillez entrer une race \n"
        }
        if(this.state.gender==""){
            errormessage=errormessage+"Veuillez entrer un sexe \n"
        }
        if(this.state.city==""){
            errormessage=errormessage+"Veuillez entrer une ville \n"
        }
        if(this.state.image==""){
            errormessage=errormessage+"Veuillez sÃ©lectionner une image \n"
        }
        if (errormessage===""){
            alert("success")
            const response =  fetch(`http://localhost:5000/addcat?name=${this.state.name}&birthdate=${this.state.birthdate}&race=${this.state.race}&gender=${this.state.gender}&city=${this.state.city}&description=${this.state.description}&image=${this.state.image}`,{method:"POST"}).
            then(res=>res.text())
            .then(text=>console.log(text));



        }
        else{
            alert(errormessage)
        }
    }
    generateimage=()=>{
        fetch('http://aws.random.cat/meow')
        .then(response => response.json())
        .then(data => {
            console.log(data)
            this.setState({...this.state, image:data.file})
        }
        )
    }



    deleteCat=(index)=>{
        fetch(`http://localhost:5000/deletecat?index=${index}`,{method:"POST"})
        .then(response => response.json())
        .then(data => {
            console.log(data)
            this.setState({...this.state, cats:data})
            }
        )

    }
    showModal=(index)=>{
        var currentcat=this.state.cats[index]
        this.setState({...this.state, currentcat:currentcat,displayModal:"block"});
    }
    async componentDidMount() {
        fetch('http://localhost:5000/getcats')
        .then(response => response.json())
        .then(data => {
            console.log(data)
            this.setState({...this.state, cats:data})
            }
        )

    }
    render() {
        return (
            <div className="admin-body">
                <table>
                    <tbody>
                        <tr className="table-header">
                        <th>IMAGE</th>
                <th>NOM</th>
        <th>DATE DE NAISSANCE</th>
        <th>RACE</th>
        <th>SEXE</th>
        <th>VILLE</th>
        <th></th>
        <th></th>
                        </tr>
                        {
this.state.cats.length>0?
this.state.cats.map((item,index) => (
    <tr>
    <td><img src={item.image} style={{height:'50px'}}/></td>
<td>{item.name}</td>
<td>{item.birthdate}</td>
<td>{item.race}</td>
<td>{item.gender}</td>
<td>{item.city}</td>
<td><i onClick={()=>this.showModal(index)} class="material-icons">edit</i>
</td>
<td><i onClick={()=>this.deleteCat(index)} style={{color:"red"}} class="material-icons">close</i>
</td>
    </tr>

)):<p>Il n'y a pas de chats, veuillez en rajouter dans la section Admin</p>

                    }


                    </tbody>
        </table>

        <div class="edit-modal" style={{display:this.state.displayModal}}>
        <div className="admin-form">
                <h1>Modifier un chat</h1>

                <div className="generate-image-div">
                {
                    (this.state.currentcat=== {})?<i class="material-icons" style={{fontSize:"200px"}}>account_circle</i>:<img style={{height:"200px"}} src={this.state.currentcat.image}/>
                }
                <button onClick={this.generateimage}>GÃ©nÃ©rer image</button>
                </div>
                <div>
                <input name="name" value={this.state.currentcat.name} onChange={this.handleChange} placeholder='Nom du chat'></input>
                </div>
                <div>
                <input type="date" value={this.state.currentcat.birthdate} onChange={this.handleChange}  name="birthdate" placeholder='Date de Naissance'></input>
                </div>
                <div>
                <input onChange={this.handleChange} value={this.state.currentcat.race}  name="race" placeholder='Race'></input>
                </div>
                <div>
                <select onChange={this.handleChange}  name="gender" placeholder='Sexe'>
                <option value="">Veuillez sÃ©lectionner un sexe</option>
                    <option value="MÃ¢le">MÃ¢le</option>
                    <option value="Femelle">Femelle</option>
                </select>
                </div>
                <div>
                <input onChange={this.handleChange}  name="city" placeholder='Ville'></input>
                </div>
                <div>
                <input onChange={this.handleChange}  name="description" placeholder='Description'></input>
                </div>
                <div>
                    <button onClick={this.handleSubmit}>Ajouter</button>
                </div>
            </div>
          </div>

        </div>
        );
    }
}

export default AdminBodyList;
