import React, { Component, Fragment } from 'react';
import CatCard from './CatCard';

class Body extends Component {
    catcards=<Fragment></Fragment>
    componentDidMount() {
        fetch('http://localhost:5000/getcats')
        .then(response => response.json())
        .then(data => {
            console.log(data)
            this.catcards=
                data.map((item) => (
                    <CatCard  name={item.name} birthdate={item.birthdate}
                    race={item.race}
                    gender={item.gender}
                    city={item.city}
                    description={item.description}
                    image={item.image}
              />
                ))
            }
        )

    }
    render() {
        return (
            <div class="body">
                <h1>Découvrez les chats près de chez vous</h1>
                <div class="cats-container">
                    {this.catcards}
                </div>
            </div>
        );
    }
}

export default Body;
