import React, { Component } from 'react';

class AdminBodyList extends Component {
    state={cats:[],catindex:0}


    handleChange=(event)=>{
        var {name,value}=event.target
        this.setState({...this.state, [name]:value})



    }
    handleSubmit=()=>{
        var errormessage=""
        if(this.state.name==""){
            errormessage=errormessage+"Veuillez entrer un nom \n"
        }
        if(this.state.birthdate==""){
            errormessage=errormessage+"Veuillez entrer une date de naissance \n"
        }
        if(this.state.race==""){
            errormessage=errormessage+"Veuillez entrer une race \n"
        }
        if(this.state.gender==""){
            errormessage=errormessage+"Veuillez entrer un sexe \n"
        }
        if(this.state.city==""){
            errormessage=errormessage+"Veuillez entrer une ville \n"
        }
        if(this.state.image==""){
            errormessage=errormessage+"Veuillez sÃ©lectionner une image \n"
        }
        if (errormessage===""){
            alert("success")
            const response =  fetch(`http://localhost:5000/addcat?name=${this.state.name}&birthdate=${this.state.birthdate}&race=${this.state.race}&gender=${this.state.gender}&city=${this.state.city}&description=${this.state.description}&image=${this.state.image}`,{method:"POST"}).
            then(res=>res.text())
            .then(text=>console.log(text));



        }
        else{
            alert(errormessage)
        }
    }
    generateimage=()=>{
        fetch('http://aws.random.cat/meow')
        .then(response => response.json())
        .then(data => {
            console.log(data)
            this.setState({...this.state, image:data.file})
        }
        )
    }



    deleteCat=(index)=>{
        fetch(`http://localhost:5000/deletecat?index=${index}`,{method:"POST"})
        .then(response => response.json())
        .then(data => {
            console.log(data)
            this.setState({...this.state, cats:data})
            }
        )

    }
    async componentDidMount() {
        fetch('http://localhost:5000/getcats')
        .then(response => response.json())
        .then(data => {
            console.log(data)
            this.setState({...this.state, cats:data})
            }
        )

    }
    render() {
        return (
            <div className="admin-body">
                <table>
                    <tbody>
                        <tr className="table-header">
                        <th>IMAGE</th>
                <th>NOM</th>
        <th>DATE DE NAISSANCE</th>
        <th>RACE</th>
        <th>SEXE</th>
        <th>VILLE</th>
        <th></th>
        <th></th>
                        </tr>
                        {
this.state.cats.length>0?
this.state.cats.map((item,index) => (
    <tr>
    <td><img src={item.image} style={{height:'50px'}}/></td>
<td>{item.name}</td>
<td>{item.birthdate}</td>
<td>{item.race}</td>
<td>{item.gender}</td>
<td>{item.city}</td>
<td><i class="material-icons">edit</i>
</td>
<td><i onClick={()=>this.deleteCat(index)} style={{color:"red"}} class="material-icons">close</i>
</td>
    </tr>

)):<p>Il n'y a pas de chats, veuillez en rajouter dans la section Admin</p>

                    }


                    </tbody>
        </table>


        </div>
        );
    }
}

export default AdminBodyList;
