import React, { Component, Fragment } from 'react';

class CatCard extends Component {
    send_adoption=()=>{
        this.setState({...this.state, icon:"schedule",backgroundColor:"grey"})

        console.log("hoho")
    }
    openModal=()=>{
        this.setState({...this.state, displayModal:"block"})
    }
    closeModal=()=>{
        this.setState({...this.state, displayModal:"none"})
    }
    state={
        name:this.props.name,
        birthdate:this.props.birthdate,
        race:this.props.race,
        gender:this.props.gender,
        city:this.props.city,
        description:this.props.description,
        image:this.props.image,
        icon:"add", backgroundColor:"red",displayModal:"none"}
    componentDidMount(){
    }
    render() {
        return (
            <Fragment>
                        <div className="card" >
                        <div className="top-card">
                                        <img src={this.state.image}></img>
                <p>{this.state.name}</p>
                        </div>
                        <div className="top-card">
                                        <div className="adopt-button" style={{backgroundColor:this.state.backgroundColor}} onClick={this.send_adoption}><i class="material-icons">{this.state.icon}</i>
                </div>
                <span onClick={this.openModal}> Plus de d√©tails</span>

                        </div>
            </div>
            <div className="modal-cat"  style={{display:this.state.displayModal}}>
                                <div className="adopt-button" style={{backgroundColor:"black"}} onClick={this.closeModal}
                                ><i class="material-icons">close</i>
            </div>
                                <img style={{height:"200px"}} src={this.state.image}></img>
                <p><b>Nom: </b>{this.state.name}</p>
                <p><b>Date de naissance: </b>{this.state.birthdate}</p>
                <p><b>Race: </b>{this.state.race}</p>
                <p><b>Sexe: </b>{this.state.gender}</p>
                <p><b>Ville: </b>{this.state.city}</p>
                <p><b>Description: </b>{this.state.description}</p>

            </div>

            </Fragment>
        );
    }
}

export default CatCard;
