import React, { Component } from 'react';

class CatCard extends Component {
    icon="add"
    send_adoption=()=>{
        this.icon="schedule"
    }
    state={img:""}
    componentDidMount(){
        fetch('http://aws.random.cat/meow')
        .then(response => response.json())
        .then(data => {
            console.log(data)
            this.setState({img:data.file})
        }
            )
    }
    render() {
        return (
            <div class="card">
                <img src={this.state.img}></img>
                <p>Nom du chat</p>
                <div class="adopt-button"><i class="material-icons">{this.icon}</i>
</div>
            </div>
        );
    }
}

export default CatCard;
